package javaPackage;

public class AssignmentOperator1 {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
		int a = 10;
		a += 5;
		System.out.println(a);
		a -= 5;
		System.out.println(a);
		a *= 3;
		System.out.println(a);
		a /= 2;
		System.out.println(a);
		
	}

}
