package javaPackage;

public class IfElse1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int p=100;
		int q=100;
		//p++;
		if (p<q){
				System.out.println("p is less than to q");
			}else if(p>q) {
				System.out.println("p is greater than to q");
			}else if(p==q) {
				System.out.println("p is equal to q");
			}else {
				System.out.println("p and q are different");
			}
			}

}