package javaPackage;

public class LogicalOperator1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 15;
		int c = 20;
		
		//Logical Operator
		System.out.println(a>b && a<c);  // False True //False
		
		//Bitwise Operator
		System.out.println(a>b & a<c); // False True = False
	}

}
